    @staticmethod
    def create_svg_icon(svg_code):
        pixmap = QPixmap(100, 100)
        pixmap.fill(Qt.transparent)
        renderer = QSvgRenderer(svg_code.encode())
        painter = QPainter(pixmap)
        renderer.render(painter)
        painter.end()
        return QIcon(pixmap)
